// Constants
const COLORS = ['#f97316', '#3b82f6', '#22c55e', '#a855f7', '#ec4899', '#eab308', '#14b8a6', '#f43f5e', '#8b5cf6', '#06b6d4'];
const FREQ_LABELS = { 1: 'daily', 7: 'weekly', 30: 'monthly', 365: 'yearly' };

// State
let strategies = [];
let strategyId = 0;
let priceChart = null;
let strategyChart = null;

// Initialize
document.addEventListener('DOMContentLoaded', () => {
  document.getElementById('qrImage').src = BTC_QR_CODE;
  updateYears();
  updateFeeExample();
});

// UI Functions
function closeIntro() {
  document.getElementById('intro').style.display = 'none';
}

function openModal() {
  document.getElementById('modalOverlay').classList.add('active');
}

function closeModal(e) {
  if (!e || e.target === document.getElementById('modalOverlay')) {
    document.getElementById('modalOverlay').classList.remove('active');
  }
}

function copyAddress() {
  navigator.clipboard.writeText('bc1qqtrezza28fhdwer5tqfyfucuqa4p7lmw8g3mcq');
  const btn = document.getElementById('copyBtn');
  btn.textContent = ' Copied!';
  btn.classList.add('copied');
  setTimeout(() => {
    btn.textContent = ' Copy Bitcoin Address';
    btn.classList.remove('copied');
  }, 3000);
}

// Update Functions
function updateYears() {
  const years = parseInt(document.getElementById('yearsSlider').value);
  document.getElementById('yearsValue').textContent = years + ' year' + (years > 1 ? 's' : '');
  const endYear = 2026;
  const startYear = endYear - years;
  document.getElementById('datesRange').textContent = '(Jan ' + startYear + '  Jan ' + endYear + ')';
  updatePriceChart();
  recalculateStrategies();
}

function updateFeeExample() {
  const pct = parseFloat(document.getElementById('feePercent').value) || 0;
  const base = parseFloat(document.getElementById('feeBase').value) || 0;
  const net = (100 - base - 100 * pct / 100).toFixed(2);
  document.getElementById('feeExample').textContent = 'Example: ' + pct + '% fee means a $100 purchase buys $' + net + ' worth of crypto.';
  recalculateStrategies();
}

// Data Functions
function getFilteredPrices() {
  const years = parseInt(document.getElementById('yearsSlider').value);
  const startIdx = btcPrices.length - (years * 12) - 1;
  return {
    btc: btcPrices.slice(Math.max(0, startIdx)),
    eth: ethPrices.slice(Math.max(0, startIdx))
  };
}

// Chart Functions
function updatePriceChart() {
  const { btc, eth } = getFilteredPrices();
  const labels = btc.map(p => p.d);

  if (priceChart) priceChart.destroy();

  priceChart = new Chart(document.getElementById('priceChart'), {
    type: 'line',
    data: {
      labels,
      datasets: [
        {
          label: ' Bitcoin',
          data: btc.map(p => p.p),
          borderColor: '#f97316',
          backgroundColor: 'rgba(249, 115, 22, 0.1)',
          yAxisID: 'y',
          tension: 0.3,
          pointRadius: 0
        },
        {
          label: ' Ethereum',
          data: eth.map(p => p.p),
          borderColor: '#3b82f6',
          backgroundColor: 'rgba(59, 130, 246, 0.1)',
          yAxisID: 'y1',
          tension: 0.3,
          pointRadius: 0
        }
      ]
    },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      interaction: { mode: 'index', intersect: false },
      plugins: { legend: { labels: { color: '#9ca3af' } } },
      scales: {
        x: { ticks: { color: '#6b7280', maxTicksLimit: 8 }, grid: { color: '#374151' } },
        y: { type: 'linear', position: 'left', ticks: { color: '#f97316', callback: v => '$' + (v / 1000).toFixed(0) + 'k' }, grid: { color: '#374151' } },
        y1: { type: 'linear', position: 'right', ticks: { color: '#3b82f6', callback: v => '$' + v }, grid: { drawOnChartArea: false } }
      }
    }
  });
}

// Strategy Functions
function addStrategy() {
  const btcAmt = parseFloat(document.getElementById('btcAmount').value) || 0;
  const ethAmt = parseFloat(document.getElementById('ethAmount').value) || 0;
  if (btcAmt === 0 && ethAmt === 0) return;

  strategies.push({
    id: ++strategyId,
    btcAmount: btcAmt,
    btcFreq: parseInt(document.getElementById('btcFrequency').value),
    ethAmount: ethAmt,
    ethFreq: parseInt(document.getElementById('ethFrequency').value)
  });

  recalculateStrategies();
}

function removeStrategy(id) {
  strategies = strategies.filter(s => s.id !== id);
  recalculateStrategies();
}

function calculateDCA(strategy) {
  const { btc, eth } = getFilteredPrices();
  const feePercent = parseFloat(document.getElementById('feePercent').value) || 0;
  const feeBase = parseFloat(document.getElementById('feeBase').value) || 0;

  let totalBTC = 0, totalETH = 0;
  let investedBTC = 0, investedETH = 0;
  let feesBTC = 0, feesETH = 0;
  const values = [];

  btc.forEach((priceData, monthIdx) => {
    const btcPrice = priceData.p;
    const ethPrice = eth[monthIdx].p;

    // BTC DCA
    if (strategy.btcAmount > 0 && monthIdx % Math.ceil(strategy.btcFreq / 30) === 0) {
      const fee = feeBase + (strategy.btcAmount * feePercent / 100);
      const net = strategy.btcAmount - fee;
      totalBTC += net / btcPrice;
      investedBTC += strategy.btcAmount;
      feesBTC += fee;
    }

    // ETH DCA
    if (strategy.ethAmount > 0 && monthIdx % Math.ceil(strategy.ethFreq / 30) === 0) {
      const fee = feeBase + (strategy.ethAmount * feePercent / 100);
      const net = strategy.ethAmount - fee;
      totalETH += net / ethPrice;
      investedETH += strategy.ethAmount;
      feesETH += fee;
    }

    const portfolioValue = (totalBTC * btcPrice) + (totalETH * ethPrice);
    values.push(portfolioValue);
  });

  const totalInvested = investedBTC + investedETH;
  const finalValue = values[values.length - 1] || 0;
  const profit = finalValue - totalInvested;
  const roi = totalInvested > 0 ? ((finalValue / totalInvested) - 1) * 100 : 0;

  return { values, totalInvested, finalValue, profit, roi, totalBTC, totalETH, fees: feesBTC + feesETH };
}

function recalculateStrategies() {
  if (strategies.length === 0) {
    document.getElementById('emptyState').style.display = 'block';
    document.getElementById('strategyChartWrapper').style.display = 'none';
    document.getElementById('resultsCard').style.display = 'none';
    return;
  }

  document.getElementById('emptyState').style.display = 'none';
  document.getElementById('strategyChartWrapper').style.display = 'block';
  document.getElementById('resultsCard').style.display = 'block';

  const { btc } = getFilteredPrices();
  const labels = btc.map(p => p.d);
  const datasets = [];
  const results = [];

  strategies.forEach((s, idx) => {
    const calc = calculateDCA(s);
    const color = COLORS[idx % COLORS.length];

    datasets.push({
      label: 'Strategy #' + (idx + 1),
      data: calc.values,
      borderColor: color,
      backgroundColor: color + '20',
      tension: 0.3,
      pointRadius: 0
    });

    results.push({ ...s, ...calc, color, index: idx });
  });

  // Update chart
  if (strategyChart) strategyChart.destroy();
  strategyChart = new Chart(document.getElementById('strategyChart'), {
    type: 'line',
    data: { labels, datasets },
    options: {
      responsive: true,
      maintainAspectRatio: false,
      interaction: { mode: 'index', intersect: false },
      plugins: { legend: { labels: { color: '#9ca3af' } } },
      scales: {
        x: { ticks: { color: '#6b7280', maxTicksLimit: 8 }, grid: { color: '#374151' } },
        y: { ticks: { color: '#9ca3af', callback: v => formatCurrency(v) }, grid: { color: '#374151' } }
      }
    }
  });

  // Update results
  const container = document.getElementById('resultsContainer');
  container.innerHTML = results.map(r => `
    <div class="strategy-row" style="border-left-color:${r.color}">
      <div class="strategy-color" style="background:${r.color}"></div>
      <span class="strategy-number">#${r.index + 1}</span>
      <div class="strategy-info">
        <div class="strategy-label">${getStrategyLabel(r)}</div>
        <div class="strategy-crypto">
          ${r.totalBTC > 0 ? '<span class="btc">' + r.totalBTC.toFixed(4) + ' BTC</span>' : ''}
          ${r.totalBTC > 0 && r.totalETH > 0 ? ' + ' : ''}
          ${r.totalETH > 0 ? '<span class="eth">' + r.totalETH.toFixed(2) + ' ETH</span>' : ''}
        </div>
      </div>
      <div class="strategy-stats">
        <div class="stat">
          <div class="stat-label">Invested</div>
          <div class="stat-value">${formatCurrency(r.totalInvested)}</div>
        </div>
        <div class="stat">
          <div class="stat-label">Value</div>
          <div class="stat-value" style="color:${r.color}">${formatCurrency(r.finalValue)}</div>
        </div>
        <div class="stat">
          <div class="stat-label">Profit</div>
          <div class="stat-value ${r.profit >= 0 ? 'green' : 'red'}">${r.profit >= 0 ? '+' : ''}${formatCurrency(r.profit)}</div>
        </div>
        <div class="stat">
          <div class="stat-label">ROI</div>
          <div class="stat-value ${r.roi >= 0 ? 'green' : 'red'}">${r.roi >= 0 ? '+' : ''}${r.roi.toFixed(0)}%</div>
        </div>
      </div>
      <button class="delete-btn" onclick="removeStrategy(${r.id})"></button>
    </div>
  `).join('');
}

// Utility Functions
function getStrategyLabel(s) {
  const parts = [];
  if (s.btcAmount > 0) parts.push('$' + s.btcAmount + ' ' + FREQ_LABELS[s.btcFreq] + ' BTC');
  if (s.ethAmount > 0) parts.push('$' + s.ethAmount + ' ' + FREQ_LABELS[s.ethFreq] + ' ETH');
  return parts.join(' + ') || 'No investment';
}

function formatCurrency(v) {
  if (v >= 1000000) return '$' + (v / 1000000).toFixed(2) + 'M';
  if (v >= 1000) return '$' + (v / 1000).toFixed(1) + 'k';
  return '$' + Math.round(v);
}
